def question2():
    Floor = 0
    count = 0
    for floor in floors:
        if floor == "(" and Floor != -1:
            Floor += 1
            count += 1
        elif floor == ")" and Floor != -1:
            Floor -= 1
            count += 1
        if Floor == -1:
            break
    print(count)        
            
            
